ToggleGrid[w_, h_] := Module[{grid, toggle, steps, target, result},
  grid = ConstantArray[1, {w, h}];
  toggle[{x_, y_}] := (
    grid[[x, y]] = 1 - grid[[x, y]];
    If[x > 1, grid[[x - 1, y]] = 1 - grid[[x - 1, y]]];
    If[x < w, grid[[x + 1, y]] = 1 - grid[[x + 1, y]]];
    If[y > 1, grid[[x, y - 1]] = 1 - grid[[x, y - 1]]];
    If[y < h, grid[[x, y + 1]] = 1 - grid[[x, y + 1]]];
  );
  steps = 0;
  target = ConstantArray[0, {w, h}];
  While[grid != target,
    For[x = 1, x <= w, x++,
      For[y = 1, y <= h, y++,
        If[grid[[x, y]] == 1, toggle[{x, y}]; steps++];
      ];
    ];
  ];
  steps
];
