findPathToEquality[a_, b_] := Module[{queue, visited, current, path, next, n, finalValue},
  queue = {{{a, b}, 1}};
  visited = Association[{{a, b} -> True}];
  While[Length[queue] > 0,
   {current, n} = First[queue];
   queue = Rest[queue];
   If[OddQ[n] && current[[1]] == current[[2]], Return[current[[1]]]];
   path = {current};
   next = {r[current], s[current]};
   Do[
    If[! KeyExistsQ[visited, next[[i]]] && next[[i, 1]] != next[[i, 2]],
     AppendTo[queue, {next[[i]], n + 1}];
     visited[next[[i]]] = True;
     ];
    , {i, 1, 2}];
   ];
  ];
