SymmetricalConvexGridPolygons[n_] := Module[{count = 0, isSymmetricalConvex},
  isSymmetricalConvex[polygon_] := Module[{vertices, center, angles},
    vertices = Sort[polygon];
    center = Mean[vertices];
    angles = VectorAngle[Subtract[#, center], {1, 0}] & /@ vertices;
    And[
      AllTrue[Differences[angles], # > 0 &],
      AllTrue[vertices, IntegerQ /@ # &],
      AllTrue[vertices, #[[1]] == center[[1]] || #[[2]] == center[[2]] &]
    ]
  ];
  Do[
    If[isSymmetricalConvex[polygon], count++],
    {polygon, Subsets[Tuples[Range[-n, n], 2], {4, 8}]}
  ];
  count
]

