CardStackingDistance[n_] := Module[{dp, i, j, k, cost},
  dp = Table[Infinity, {n}, {n}];
  Do[dp[[i, i]] = 0, {i, n}];
  Do[
    Do[
      cost = j - i;
      Do[
        dp[[i, j]] = Min[dp[[i, j]], dp[[i, k]] + dp[[k + 1, j]] + cost],
        {k, i, j - 1}
      ],
      {i, j - 1, -1, -1}
    ],
    {j, 2, n}
  ];
  dp[[1, n]]
]

