(* Define a function to compute D(N) using dynamic programming *)
D[n_] := D[n] = Module[{dp, i, j, k, mod = 10^9},
  dp = Table[0, {n + 1}, {n + 1}, {n + 1}];
  dp[[1, 1, 1]] = 1;
  For[i = 1, i <= n, i++,
    For[j = 1, j <= n - i + 1, j++,
      For[k = 1, k <= n - i - j + 2, k++,
        dp[[i + 1, j, k]] = (dp[[i + 1, j, k]] + dp[[i, j, k]]) mod mod;
        dp[[i, j + 1, k]] = (dp[[i, j + 1, k]] + dp[[i, j, k]]) mod mod;
        dp[[i, j, k + 1]] = (dp[[i, j, k + 1]] + dp[[i, j, k]]) mod mod;
      ]
    ]
  ];
  dp[[n + 1, 1, 1]]
];

