(* Define the function to calculate the number of reachable configurations for a given grid size *)
reachableConfigurations[m_, n_] := Module[{grid, emptyPos, move, moves, visited, queue, current, newPos, newGrid, result},
  grid = Array[1 &, {m, n}];
  grid[[m, n]] = 0; (* Set the bottom-right corner as the empty space *)
  emptyPos = {m, n};
  moves = {{1, 0}, {-1, 0}, {0, 1}, {0, -1}};
  visited = <|grid -> True|>;
  queue = {{grid, emptyPos}};
  result = 0;
  While[Length[queue] > 0,
   {current, emptyPos} = First[queue];
   queue = Rest[queue];
   result++;
   Do[
    newPos = emptyPos + move;
    If[1 <= newPos[[1]] <= m && 1 <= newPos[[2]] <= n,
     newGrid = current;
     newGrid[[emptyPos[[1]], emptyPos[[2]]]] = newGrid[[newPos[[1]], newPos[[2]]]];
     newGrid[[newPos[[1]], newPos[[2]]]] = 0;
     If[! KeyExistsQ[visited, newGrid],
      AppendTo[queue, {newGrid, newPos}];
      visited[newGrid] = True;
      ];
     ];
    , {move, moves}];
   ];
  result
  ]

(* Calculate the number of reachable configurations for the given grid size *)
