MinTrianglesInRange[aMax_, bMax_] := Module[{minTriangles, a, b, triangles},
  minTriangles = Infinity;
  For[a = 1, a <= aMax, a += 0.01,
    For[b = 1, b <= bMax, b += 0.01,
      triangles = NumberOfTriangles[a, b];
      If[triangles > 0 && triangles < minTriangles, minTriangles = triangles]
    ]
  ];
  minTriangles
]

