(* Define the vertices of the quadrilateral based on the given angles and equal sides *)
A = {0, 0};
B = {1, 0};
D = {1/2, Sqrt[3]/2};
C = {1/2, 1 + Sqrt[3]/2};

(* Function to reflect a point across a line defined by two points *)
reflectPoint[{px_, py_}, {x1_, y1_}, {x2_, y2_}] := Module[
  {dx, dy, a, b, c, d, det, x, y},
  dx = x2 - x1;
  dy = y2 - y1;
  a = (dx^2 - dy^2)/(dx^2 + dy^2);
  b = 2 dx dy/(dx^2 + dy^2);
  c = 2 (x1 dy - y1 dx)/(dx^2 + dy^2);
  d = 2 (y1 dx - x1 dy)/(dx^2 + dy^2);
  det = a^2 + b^2;
  x = a px + b py + c;
  y = b px - a py + d;
  {x, y}
];

(* Simulate the billiard path starting from a point and direction *)
simulateBilliardPath[start_, direction_, n_] := Module[
  {path, currentPoint, currentDirection, nextPoint, nextDirection, edges, edge, i},
  path = {start};
  currentPoint = start;
  currentDirection = direction;
  edges = {{A, B}, {B, C}, {C, D}, {D, A}};
  For[i = 1, i <= n, i++,
   edge = First[Select[edges, 
      RegionDistance[InfiniteLine[#, #2], 
        currentPoint + currentDirection] < 10^-6 &]];
   nextPoint = 
    RegionNearest[InfiniteLine @@ edge, 
     currentPoint + currentDirection];
   nextDirection = 
    reflectPoint[currentPoint + currentDirection, edge[[1]], 
     edge[[2]]] - nextPoint;
   AppendTo[path, nextPoint];
   currentPoint = nextPoint;
   currentDirection = nextDirection;
   ];
  path
];

(* Calculate the path and find the number of bounces *)
