H[n_] := Module[{winning, gcd, a, b, c, d, adbc, result},
  winning = ConstantArray[False, {n + 1, n + 1}];
  gcd = Table[GCD[a, b], {a, 0, n}, {b, 0, n}];
  Do[
    If[gcd[[a + 1, b + 1]] == 1,
      If[a == 0 || b == 0,
        winning[[a + 1, b + 1]] = True,
        If[!AnyTrue[
          Flatten[Table[
            adbc = a d - b c;
            If[adbc == 1 || adbc == -1,
              winning[[a - c + 1, b - d + 1]],
              False
            ],
            {c, 0, a}, {d, 0, b}
          ]],
          TrueQ
        ],
          winning[[a + 1, b + 1]] = True
        ]
      ]
    ],
    {a, 0, n}, {b, 0, n}
  ];
  result = Total[Boole[winning[[a + 1, b + 1]] && gcd[[a + 1, b + 1]] == 1 && a + b <= n], {a, 1, n}, {b, 1, n}];
