(* Define the function to calculate the Grundy number for a given position *)
GrundyNumber[position_] := Module[{pegs, nimSum},
  pegs = Total[#, {2}] & /@ position;
  nimSum = BitXor @@ pegs;
  If[nimSum == 0, 0, 1]
]

(* Generate the sequence of positions for the Towers of Hanoi solution *)
HanoiPositions[n_] := Module[{positions, move, from, to, via, moveDisk},
  positions = {{Table[n, {n}], {}, {}}};
  moveDisk[pos_, from_, to_] := Module[{newPos = pos},
    AppendTo[newPos[[to]], Last[newPos[[from]]]];
    newPos[[from]] = Most[newPos[[from]]];
    newPos
  ];
  move[1, from_, to_, via_] := (
    AppendTo[positions, moveDisk[Last[positions], from, to]]
  );
  move[k_, from_, to_, via_] := (
    move[k - 1, from, via, to];
    move[1, from, to, via];
    move[k - 1, via, to, from]
  );
  move[n, 1, 3, 2];
  positions
]

(* Calculate f(n) *)
f[n_] := Module[{positions, losingIndices},
  positions = HanoiPositions[n];
  losingIndices = Select[Range[0, 2^n - 1], 
    GrundyNumber[positions[[# + 1]]] == 0 &];
  Total[losingIndices]
]

