simulatePiles[n_, trials_] := Module[{maxPiles, piles, deck, card, i},
  maxPiles = 0;
  Do[
    piles = <||>;
    deck = Flatten[Table[ConstantArray[i, 4], {i, n}]];
    deck = RandomSample[deck];
    Do[
      card = deck[[i]];
      If[KeyExistsQ[piles, card],
        piles[card] += 1,
        piles[card] = 1
      ];
      If[piles[card] == 4, KeyDropFrom[piles, card]];
      maxPiles = Max[maxPiles, Length[piles]];
      , {i, Length[deck]}
    ];
    , {trials}
  ];
