findMagicBean[a_, b_, c_] := Module[{dp, i, j, k, x, y, z},
  dp = ConstantArray[Infinity, {a + 1, b + 1, c + 1}];
  dp[[0 + 1, 0 + 1, 0 + 1]] = 0;
  For[i = 0, i <= a, i++,
   For[j = 0, j <= b, j++,
    For[k = 0, k <= c, k++,
     If[i + j + k > 0,
      For[x = 0, x <= i, x++,
       dp[[i + 1, j + 1, k + 1]] = 
        Min[dp[[i + 1, j + 1, k + 1]], 
         1 + Max[If[x > 0, dp[[x, j + 1, k + 1]], 0], 
           If[i - x > 0, dp[[i - x + 1, j + 1, k + 1]], 0]]];
       ];
      For[y = 0, y <= j, y++,
       dp[[i + 1, j + 1, k + 1]] = 
        Min[dp[[i + 1, j + 1, k + 1]], 
         1 + Max[If[y > 0, dp[[i + 1, y, k + 1]], 0], 
           If[j - y > 0, dp[[i + 1, j - y + 1, k + 1]], 0]]];
       ];
      For[z = 0, z <= k, z++,
       dp[[i + 1, j + 1, k + 1]] = 
        Min[dp[[i + 1, j + 1, k + 1]], 
         1 + Max[If[z > 0, dp[[i + 1, j + 1, z]], 0], 
           If[k - z > 0, dp[[i + 1, j + 1, k - z + 1]], 0]]];
       ];
      ];
     ];
    ];
   ];
  dp[[a + 1, b + 1, c + 1]]
  ]
