ClearAll[S, optimalPlay];
optimalPlay[a_, b_, rounds_] := Module[{dp, i, j, k, l, m, n, minArea, maxArea},
  dp = ConstantArray[Infinity, {a, b, rounds + 1}];
  dp[[1, 1, 1]] = 1;
  For[i = 1, i <= a, i++,
   For[j = 1, j <= b, j++,
    For[k = 2, k <= rounds, k++,
     minArea = Infinity;
     For[m = 1, m <= i, m++,
      For[n = 1, n <= j, n++,
       maxArea = Max[
         If[m > 1, dp[[m - 1, j, k - 1]], 0],
         If[n > 1, dp[[i, n - 1, k - 1]], 0],
         If[m < i, dp[[i - m, j, k - 1]], 0],
         If[n < j, dp[[i, j - n, k - 1]], 0]
         ];
       minArea = Min[minArea, maxArea]
       ]
      ];
     dp[[i, j, k]] = minArea
     ]
    ]
   ];
  dp[[a, b, rounds]]
  ];
