PermuteBells[letters_] := Module[{n = Length[letters], perms = {letters}, current = letters, largest, pos, newPerm},
  While[Length[perms] < n!,
    largest = Max[Select[Range[n], ! MemberQ[perms, Swap[current, #]] &]];
    pos = Position[current, largest][[1, 1]];
    newPerm = If[pos < n && ! MemberQ[perms, Swap[current, pos + 1]], 
      Swap[current, pos + 1], Swap[current, pos - 1]];
    AppendTo[perms, newPerm];
    current = newPerm;
  ];
  Length[perms] - 1
];

Swap[list_, i_] := Module[{temp = list},
