(* Define a function to check if a point is on the hexagonal lattice *)
OnHexagonalLatticeQ[{x_, y_}] := Mod[x + y, 2] == 0

(* Define a function to calculate the inradius of a triangle given its vertices *)
Inradius[{a_, b_, c_}] := Module[{s, A, B, C},
  A = Norm[b - c];
  B = Norm[a - c];
  C = Norm[a - b];
  s = (A + B + C)/2;
  Sqrt[(s - A) (s - B) (s - C)/s]
]

(* Define a function to check if a triangle is remarkable *)
RemarkableTriangleQ[{a_, b_, c_}] := Module[{angles, incenter},
  angles = {VectorAngle[b - a, c - a], VectorAngle[a - b, c - b], VectorAngle[a - c, b - c]};
  incenter = (Norm[b - c] a + Norm[a - c] b + Norm[a - b] c)/(Norm[b - c] + Norm[a - c] + Norm[a - b]);
  AnyTrue[angles, Abs[# - Pi/3] < 10^-6 &] && OnHexagonalLatticeQ[incenter]
]

(* Generate all possible triangles with vertices on the hexagonal lattice within a given radius *)
GenerateTriangles[r_] := Module[{points, triangles},
  points = Select[Flatten[Table[{x, y}, {x, -r, r}, {y, -r, r}], 1], OnHexagonalLatticeQ];
  triangles = Subsets[points, {3}];
  Select[triangles, RemarkableTriangleQ]
]

(* Count the number of remarkable triangles with inradius <= r *)
T[r_] := Length[Select[GenerateTriangles[Ceiling[2 r]], Inradius[#] <= r &]]

(* Compute the result for T(10^6) *)
