(* Define the modulo constant *)
mod = 10^9 + 7;

(* Function to compute factorial modulo mod *)
factorialMod[n_, mod_] := Module[{res = 1},
  Do[res = Mod[res * i, mod], {i, 1, n}];
  res
]

(* Function to compute the rank of a permutation *)
rankOfPermutation[perm_] := Module[{n = Length[perm], rank = 1, fact, count},
  fact = factorialMod[n - 1, mod];
  Do[
    count = Count[perm[[i + 1 ;;]], _?(# < perm[[i]] &)];
    rank = Mod[rank + count * fact, mod];
    If[i < n - 1, fact = Mod[fact/(n - i), mod]],
    {i, 1, n - 1}
  ];
  rank
]

(* Function to compute Q(n) *)
Q[n_] := Module[{perms, sum = 0, perm, i, permPower, rank},
  perms = Permutations[Range[n]];
  Do[
    perm = perms[[k]];
    permPower = perm;
    Do[
      rank = rankOfPermutation[permPower];
      sum = Mod[sum + rank, mod];
      permPower = permPower[[perm]],
      {i, 1, Length[perms]}
    ],
    {k, 1, Length[perms]}
  ];
  sum
]

