colMajor[n_, m_] := Flatten[Table[(j - 1)*n + i, {j, m}, {i, n}]]
swapCount[perm_] := Module[{visited, cycle, count},
  visited = ConstantArray[False, Length[perm]];
  count = 0;
  Do[
    If[!visited[[i]],
      cycle = i;
      While[!visited[[cycle]],
        visited[[cycle]] = True;
        cycle = perm[[cycle]];
      ];
      count += 1;
    ],
    {i, Length[perm]}
  ];
  Length[perm] - count
]
